mapper.select:
SELECT A.id,A.topic,A.serial_no,A.plan_comp_time,A.hope_comp_time,A.created_person,A.job_number,C.FDeptFullName AS deptName,A.project_name,D.product_name AS model,A.created_at as createdAt,A.matter_date,A.sn_no,A.real_op_time,A.finish_date_time, A.ultimate_customer,A.problem_type,A.problem_attach,A.problem_desc,A.problem_path,A.site_condition,A.rescue_action,A.fae_header,A.finish_user_id,A.finish_code,A.close_date_time,A.close_user_id,A.close_score,A.cancel_date_time,A.cancel_user_id, A.work_order_type,G.FHumanName AS receiverName,A.receiver,E.name as typeDesc,A.order_status,F.name as statusDesc,A.service_catalog_id,H.catalogName AS serviceCatalogDesc,A.order_type FROM crm_work_order A LEFT JOIN tsysuser B ON A.job_number=B.FUserName LEFT JOIN tsysdept C ON B.FDeptId=C.FSourceCode LEFT JOIN crm_product D ON A.model=D.crm_id LEFT JOIN trequestCategory E ON E.id=A.work_order_type LEFT JOIN orderStatus F ON F.id=A.order_status LEFT JOIN tsysuser G ON A.receiver=G.FUserName LEFT JOIN subServiceCatalog H ON A.service_catalog_id=H.catalogId where 1=1
AND A.topic LIKE CONCAT('%',#{topic},'%')
AND A.serial_no LIKE CONCAT('%',#{serialNo},'%')
AND A.order_status in
#{orderStatus}
AND A.model = #{model}
AND A.work_order_type in
#{workOrderType}
AND A.created_at >= #{startTime}
AND A.created_at <= #{endTime}
ORDER BY A.created_at desc

SELECT A.id,A.topic,A.serial_no,A.plan_comp_time,A.hope_comp_time,A.created_person,A.job_number,C.FDeptFullName AS deptName,A.project_name,D.product_name AS model,A.created_at as createdAt,A.matter_date,A.sn_no,A.real_op_time,A.finish_date_time, A.ultimate_customer,A.problem_type,A.problem_attach,A.problem_desc,A.problem_path,A.site_condition,A.rescue_action,A.fae_header,A.finish_user_id,A.finish_code,A.close_date_time,A.close_user_id,A.close_score,A.cancel_date_time,A.cancel_user_id, A.work_order_type,G.FHumanName AS receiverName,A.receiver,E.name as typeDesc,A.order_status,F.name as statusDesc,A.service_catalog_id,H.catalogName AS serviceCatalogDesc,A.order_type FROM crm_work_order A LEFT JOIN tsysuser B ON A.job_number=B.FUserName LEFT JOIN tsysdept C ON B.FDeptId=C.FSourceCode LEFT JOIN crm_product D ON A.model=D.crm_id LEFT JOIN trequestCategory E ON E.id=A.work_order_type LEFT JOIN orderStatus F ON F.id=A.order_status LEFT JOIN tsysuser G ON A.receiver=G.FUserName LEFT JOIN subServiceCatalog H ON A.service_catalog_id=H.catalogId where A.job_number= #{jobNumber}
AND A.topic LIKE CONCAT('%',#{topic},'%')
AND A.serial_no LIKE CONCAT('%',#{serialNo},'%')
AND A.order_status in
#{orderStatus}
AND A.model = #{model}
AND A.work_order_type in
#{workOrderType}
