mapper.update:
UPDATE serviceCatalog
catalogName = #{catalogName}, shortName = #{shortName}, icon = #{icon}, status = #{status}, modifyDateTime = #{modifyDateTime}, modifyUserId = #{modifyUserId}
WHERE catalogId = #{catalogId}

mapper.select:
SELECT count(*)
FROM subServiceCatalog A
where A.status=1 AND A.parentId = #{catalogId}
