mapper.select:
SELECT A.catalogId,A.catalogName,A.description,A.parentId,A.status,A.defaultQueueId,D.name as defaultQueue ,A.createUserId,A.createDateTime,B.FHumanName as createPerson ,A.modifyDateTime,A.modifyUserId,C.FHumanName as modifyPerson FROM subServiceCatalog A LEFT JOIN tsysuser B ON A.createUserId=B.FUserName LEFT JOIN tsysuser C ON A.modifyUserId=C.FUserName LEFT JOIN trequestqueues D ON A.defaultQueueId=D.id
Where A.parentId= #{ParentId}
ORDER BY A.createDateTime DESC

mapper.update:
UPDATE subServiceCatalog
catalogName = #{catalogName}, description = #{description}, status = #{status}, icon = #{icon}, defaultQueueId = #{defaultQueueId}, modifyDateTime = #{modifyDateTime}, modifyUserId = #{modifyUserId}
WHERE catalogId = #{catalogId}

mapper.select:
SELECT count(*)
FROM crm_work_order A
where A.status not in(5,7) AND A.service_catalog_id = #{catalogId}
